mapper.select:
SELECT USER_PWD 
FROM TB_USER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE TB_USER
SET USER_PWD = #{newPassword}
WHERE USER_ID = #{userId}

UPDATE TB_USER
USER_NAME = #{userName}, NICKNAME = #{nickname}, POSTCODE = #{postcode}, MAIN_ADDRESS = #{mainAddress}, DETAIL_ADDRESS = #{detailAddress},
WHERE USER_ID = #{userId}

UPDATE TB_USER
SET MODIFY_PROFILE = #{modifyProfile} 
WHERE USER_ID = #{userId}

UPDATE TB_USER
SET EMAIL = #{newEmail}
WHERE USER_ID = #{userId}

UPDATE TB_USER
SET PHONE = #{newPhone}
WHERE USER_ID = #{userId}
