mapper.insert:
INSERT INTO ORDERS (
    ORDER_NO, ORDER_AMOUNT, ORDER_STATUS, 
    DELIVERY_STATUS, POSTCODE, ADDRESS, USER_NO
) VALUES (
    #{orderNo}, #{orderAmount}, 'PAY',
    'READY', #{postcode}, #{address}, #{userNo}
)

INSERT INTO ORDER_DETAIL (
    QUANTITY, ORDER_NO, OPTION_NO
) VALUES (
    #{quantity}, #{orderNo}, #{optionNo}
)

mapper.select:
SELECT PRODUCT_NO
FROM TB_OPTION
WHERE OPTION_NO = #{optionNo}

mapper.update:
UPDATE PRODUCT
SET CURRENT_AMOUNT = CURRENT_AMOUNT + #{amount}
WHERE PRODUCT_NO = #{productNo}
