mapper.select:
SELECT SELLER_NO
FROM SELLER_PROFILE
WHERE USER_NO = #{userNo}

mapper.insert:

    SELECT SEQ_SELLER_PROFILE_NO.NEXTVAL FROM DUAL

INSERT INTO SELLER_PROFILE (
    SELLER_NO,
    INTRODUCTION,
    USER_NO
) VALUES (
    #{sellerNo},
    NVL(#{introduction}, '신규 메이커입니다.'),
    #{userNo}
)
